@js:
function b64Url(item_id) {
  const url =
    book.type === 32
      ? `https://reading.snssdk.com/reading/reader/audio/playinfo/?tone_id=1&item_ids=${item_id}&pv_player=-1&aid=1967`
      : `data:;base64,${java.base64Encode(item_id)},{"type":"pyfqc"}`;
  return String(url);
}

let type = +java.get("btype");
switch (type) {
  case 1:
    book.type = 64;
    break;
  case 4:
    book.type = 32;
    break;
  default:
    book.type = 8;
}
let bid = java.hexDecodeToString(result);
let list = [];
if (bid.includes("/")) {
  list.push({ title: "正文", url: b64Url(bid) })
} else {
  try {
    // 网页接口
    let u = "https://fanqienovel.com/api/reader/directory/detail?bookId=" + bid;
    let items = org.jsoup.Jsoup.connect(u).timeout(5e3).ignoreContentType(true).execute().body();
    let List = JSON.parse(items).data.chapterListWithVolume;
    List.forEach($ => {
      $.forEach((_, i) => {
        let name = _.volume_name || "第一卷：默认";
        let time = java.timeFormat(_.firstPassTime * 1000);
        if (!i && book.type !== 32) list.push({ title: name, url: "", vo: true });
        list.push({ title: _.title, vo: false, url: b64Url(_.itemId), info: `${name} | ${time}` });
      });
    });
    // throw new Error("api test");
  } catch (e) {
    java.log(e)
  }
}
