exploreUrl:
@js: var result = [];
java.toast("🔥动态获取发现中……");
var push=(title,url,size)=>result.push({
  	"title": title,
  	"url": url,
  	"style": {
   "layout_flexGrow": 1,
   "layout_flexBasisPercent": size
   }
 });
var top = java.ajax("https://www.kuku3.com/rank");
var tag = java.ajax("https://www.kuku3.com/quanban/sort/")
if (top&&tag) {
a=org.jsoup.Jsoup.parse(top).select(".sortChannel_nav a");
b=org.jsoup.Jsoup.parse(tag).select(".sortChannel_nav a");
push("排行榜",null,1)
    for(i in a){
        title=a[i].text()
        url=a[i].attr("href")
        size=0.4;
  //url=String(url).replace("1","{{page}}")
        push(title,url,size)
    };
    push("分类",null,1)
    for(i in b){
        title=b[i].text()
        url=b[i].attr("href")
        size=0.25
        if (url.endsWith("sort/")) {
            url=url+"1"
            url=String(url).replace("1","{{page}}")
            push(title,url,size)
        } else if (url.endsWith("sort/1/1/")) {
            url=String(url).replace(url,"sort/1/{{page}}")
            push(title,url,size)
        } else {
            url=String(url).replace("1","{{page}}")
            push(title,url,size)
    }}
      } else { 
      java.toast("🚫发现获取失败……");
}
JSON.stringify(result);

